import React, { useContext } from 'react';
import './portfolio.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import 'swiper/css/parallax';
import 'swiper/css/effect-cards';
import 'swiper/css/effect-flip'; // Import effect-flip for a cool flip effect

// Import images with the correct paths
import Ecom from '../assets/img/ecommerce.png';
import Music from '../assets/img/musicapp.png';
import Hoc from '../assets/img/hoc.png';
import Trade from '../assets/img/tradeapp.jpeg';

import { themeContext } from "../Context";

const Portfolio = () => {
  const theme = useContext(themeContext);
  const darkTheme = theme.state.darkmode;

  return (
    <div className="ProjectsSwiper" id="Projects">
      <span style={{ color: darkTheme ? 'white' : 'black' }}>My Recent</span>
      <span>Projects</span>

      <Swiper
        spaceBetween={50} // Add some space between slides
        slidesPerView={1.5} // Number of slides per view
        centeredSlides={true} // Center the slides
        grabCursor={true} // Cursor grabs the slide
        navigation={true} // Enable navigation arrows
        pagination={{ clickable: true }} // Clickable pagination
        autoplay={{ delay: 2500 }} // Autoplay with delay
        parallax={true} // Enable parallax effect
        effect="flip" // Apply the flip effect to slides
        loop={true} // Make it loop infinitely
        modules={['Navigation', 'Pagination', 'Autoplay', 'EffectFlip', 'Parallax']} // Register necessary modules
        className="ProjectsSwiper-slider"
      >
        <SwiperSlide>
          <div className="slide-content">
            <img src={Ecom} alt="E-commerce project" className="project-image" />
            <p className="project-title">E-commerce Project</p>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="slide-content">
            <img src={Music} alt="Music App project" className="project-image" />
            <p className="project-title">Music App Project</p>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="slide-content">
            <img src={Hoc} alt="HOC project" className="project-image" />
            <p className="project-title">HOC Project</p>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="slide-content">
            <img src={Trade} alt="Trade App project" className="project-image" />
            <p className="project-title">Trade App Project</p>
          </div>
        </SwiperSlide>

        <div className="slider-controler">
          <div className="swiper-button-prev slider-arrow">
            <ion-icon name="arrow-back-outline"></ion-icon>
          </div>
          <div className="swiper-button-next slider-arrow">
            <ion-icon name="arrow-forward-outline"></ion-icon>
          </div>
          <div className="swiper-pagination"></div>
        </div>
      </Swiper>
    </div>
  );
}

export default Portfolio;
