import React, { useContext } from 'react';
import './portfolio.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Scrollbar, A11y } from 'swiper/modules'; // Correct imports

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

// Import images
import Ecom from '../assets/img/ecommerce.png';
import Music from '../assets/img/musicapp.png';
import Hoc from '../assets/img/hoc.png';
import Trade from '../assets/img/tradeapp.jpeg';
import { themeContext } from "../Context";

const Portfolio = () => {
  const theme = useContext(themeContext);
  const darkTheme = theme.state.darkmode;

  return (
    <div className="ProjectsSwiper" id="Projects">
      <span style={{ color: darkTheme ? 'white' : 'black' }}>My Recent</span>
      <span>Projects</span>

      <Swiper
        // Install Swiper modules
        modules={[Navigation, Pagination, Scrollbar, A11y]} // Using the necessary modules
        spaceBetween={30} // Space between slides
        slidesPerView={2.5} // Number of slides per view
        grabCursor={true} // Enable cursor grab
        navigation={true} // Enable navigation
        pagination={{ clickable: true }} // Enable clickable pagination
        scrollbar={{ draggable: true }} // Enable draggable scrollbar
        onSwiper={(swiper) => console.log(swiper)} // Log Swiper instance
        onSlideChange={() => console.log('slide change')} // Log slide change event
        className="ProjectsSwiper-slider"
      >
        {/* Slides with project images */}
        <SwiperSlide>
          <img src={Ecom} alt="E-commerce project" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Music} alt="Music App project" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Hoc} alt="HOC project" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Trade} alt="Trade App project" />
        </SwiperSlide>
      </Swiper>
    </div>
  );
}

export default Portfolio;
