import React, { useState, useContext } from 'react';
import './Navbar.css';
import Toggle from './Toggle';
import { Link } from 'react-scroll';
import { themeContext } from '../Context';
import { FaBars, FaTimes } from 'react-icons/fa'; // Importing icons

const Navbar = () => {
  const [sidebar, setSidebar] = useState(false); // Sidebar state
  const theme = useContext(themeContext);
  const darkTheme = theme.state.darkmode;

  const toggleSidebar = () => {
    setSidebar(!sidebar); // Toggle sidebar visibility
  };

  return (
    <div className="nav-wrapper" id="Navbar">
      <div className="nav-left">
        <div className="n-name">Damodhar</div>
        <Toggle />
        <div className="hamburger-menu" onClick={toggleSidebar}>
          {sidebar ? <FaTimes size={24} /> : <FaBars size={24} />} {/* Toggle icon */}
        </div>
      </div>

      <div className={sidebar ? 'side_bar' : 'nav-right'}>
        <ul className="nav-list" style={{ listStyleType: 'none' }}>
          <Link
            style={{ color: darkTheme ? 'white' : 'black' }}
            spy={true}
            to="Navbar"
            smooth={true}
            onClick={() => setSidebar(false)} // Close sidebar after clicking a link
          >
            <li>Home</li>
          </Link>
          <Link
            style={{ color: darkTheme ? 'white' : 'black' }}
            spy={true}
            to="Services"
            smooth={true}
            onClick={() => setSidebar(false)}
          >
            <li>Services</li>
          </Link>
          <Link
            style={{ color: darkTheme ? 'white' : 'black' }}
            spy={true}
            to="Experience"
            smooth={true}
            onClick={() => setSidebar(false)}
          >
            <li>Experience</li>
          </Link>
          <Link
            style={{ color: darkTheme ? 'white' : 'black' }}
            spy={true}
            to="Projects"
            smooth={true}
            onClick={() => setSidebar(false)}
          >
            <li>Projects</li>
          </Link>
          <Link
            style={{ color: darkTheme ? 'white' : 'black' }}
            spy={true}
            to="Contact"
            smooth={true}
            onClick={() => setSidebar(false)}
          >
            <li>Contact</li>
          </Link>
        </ul>
      </div>
    </div>
  );
};

export default Navbar;
