import React, { useContext } from 'react';
import './portfolio.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

// Import Swiper modules
import { Navigation, Pagination, Scrollbar } from 'swiper';

// Import images
import Ecom from '../assets/img/ecommerce.png';
import Music from '../assets/img/musicapp.png';
import Hoc from '../assets/img/hoc.png';
import Trade from '../assets/img/tradeapp.jpeg';
import { themeContext } from "../Context";

const Portfolio = () => {
  const theme = useContext(themeContext);
  const darkTheme = theme.state.darkmode;

  return (
    <div className="ProjectsSwiper" id="Projects">
      <span style={{ color: darkTheme ? 'white' : 'black' }}>My Recent</span>
      <span>Projects</span>

      <Swiper
        modules={[Navigation, Pagination, Scrollbar]}  // Adding Swiper modules
        spaceBetween={30}  // Space between slides
        slidesPerView={2.5}  // Number of slides visible at once
        grabCursor={true}  // Enable grab cursor
        navigation={true}  // Enable navigation arrows
        pagination={{ clickable: true }}  // Enable clickable pagination bullets
        scrollbar={{ draggable: true }}  // Enable draggable scrollbar
        loop={true}  // Enable infinite looping of slides
        className="ProjectsSwiper-slider"
      >
        <SwiperSlide>
          <img src={Ecom} alt="E-commerce project" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Music} alt="Music App project" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Hoc} alt="HOC project" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Trade} alt="Trade App project" />
        </SwiperSlide>
      </Swiper>
    </div>
  );
};

export default Portfolio;
